pragma solidity ^0.4.18;

contract EdsToken {  
	//public variables of token
	string public name;
	string public symbol;
	uint public decimals;


	//keep track of who owns how much eds token
	mapping(address => uint256 )  public EdsBalance;

	//initialize contract with initial supply token to creator of contract
	function EdsToken (uint256 _supply, string _name, string _symbol, uint8 _decimals ) { 
		EdsBalance [ msg.sender ] = _supply;
		name = _name;
		symbol = _symbol;
		decimals = _decimals;	
	}



	event Transfer (address indexed from, address indexed to, uint256 value );


	//Send coins
	function transfer ( address _to, uint256 _value )  {
		require (EdsBalance[ msg.sender ] >= _value );
		EdsBalance[ msg.sender ] -= _value;
		EdsBalance[ _to ] += _value;

		Transfer ( msg.sender, _to, _value );
	}
}
